    private static Vector<Action> createActionVector(String... actions) {
        return new Vector<>(Arrays.stream(actions).sorted().map(Action::new).toList());
    }

    private static Vector<Distribution<Observation>> createObservationVector(String... observations) {
        return new Vector<>(Arrays.stream(observations)
                .sorted()
                .map(Observation::new)
                .map(Distribution::createSingleEntryDistribution)
                .toList()
        );
    }

